package com.wilb0t.aoc;

import static org.hamcrest.CoreMatchers.is;
import static org.hamcrest.MatcherAssert.assertThat;

import org.junit.jupiter.api.Test;

public class Day6Test {

  private static final String PUZZLE_INPUT = "";
  
  @Test
  public void testGetStartOfPackerMarker_test() {
    assertThat(Day6.getStartOfPacketMarker("mjqjpqmgbljsphdztnvjfqwrcgsmlb"), is(7));
    assertThat(Day6.getStartOfPacketMarker("bvwbjplbgvbhsrlpgdmjqwftvncz"), is(5));
    assertThat(Day6.getStartOfPacketMarker("nppdvjthqldpwncqszvftbrmjlhg"), is(6));
    assertThat(Day6.getStartOfPacketMarker("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"), is(10));
    assertThat(Day6.getStartOfPacketMarker("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"), is(11));
  }
  
  @Test
  public void testGetStartOfPacketMarker_puzzle() {
    assertThat(Day6.getStartOfPacketMarker(PUZZLE_INPUT), is(1794));
  }
  
  @Test
  public void testGetStartOfMsgMarker_test() {
    assertThat(Day6.getStartOfMsgMarker("mjqjpqmgbljsphdztnvjfqwrcgsmlb"), is(19));
    assertThat(Day6.getStartOfMsgMarker("bvwbjplbgvbhsrlpgdmjqwftvncz"), is(23));
    assertThat(Day6.getStartOfMsgMarker("nppdvjthqldpwncqszvftbrmjlhg"), is(23));
    assertThat(Day6.getStartOfMsgMarker("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"), is(29));
    assertThat(Day6.getStartOfMsgMarker("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"), is(26));
  }

  @Test
  public void testGetStartOfMsgMarker_puzzle() {
    assertThat(Day6.getStartOfMsgMarker(PUZZLE_INPUT), is(2851));
  }

}
